import React from 'react';

class LastJob extends React.Component {
  constructor() {
    super()
    this.state = {
      cv: '',
      job: '',
      description: ''
    }
  }

  handleChanges = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  render() {
    return (
      <fieldset>
        <label> Resumo do currículo
          <textarea maxLength="1000" required name="cv" />
        </label>

        <label> Cargo
          <textarea maxLength="40" required name="job" onMouseEnter={() => alert("Preencha com cuidado esta informação.")} />
        </label>

        <label> Descrição do cargo
          <input type="text" maxLength="500" required name="description" />
        </label>

      </fieldset>
    )
  }
}

export default LastJob;
